def newTweet(expandedTweet):
    completion = client.chat.completions.create(
        model="gpt-4o",
        messages=[
            {
                "role": "system",
                "content": expandedTweet,
            },
            {
                "role": "user",
                "content": "Task, Goal, or Current Prompt:\n" + expandedTweet,
            },
        ],
    )

    return completion.choices[0].message.content

